INSERT INTO `tkp` (`id`, `soal`, `pilihan_a`, `pilihan_b`, `pilihan_c`, `pilihan_d`, `pilihan_e`, `skor5`, `skor4`, `skor3`, `skor2`, `skor1`, `jawaban`) VALUES
(1, 'Saya mengunakan kendaraan dinas tanpa sepengatahuan Kepala Kendaraan tersebut pada hari libur. Secara tidak sengaja saya menabrakkan kendaraan tersebut. Tindakan saya adalah ...', 'Mencoba memperbaiki sendiri kendaraan tersebut.', 'Melaporkan kejadian tersebut kepada pimpinan dan siap menerima hukuman/petunjuk dari pimpinan.', 'Membawa kendaraan tersebut ke bengkel atas biaya pribadi dan mengembalikannya dengan diam-diam.', 'Diam-diam menyimpan kendaraan tersebut karena tidak seorang pegawai pun yang tahu kalausaya menggunakannya.', 'Membawa kendaraan tersebut ke bengkel, melaporkan kejadian tersebut kepada pimpinan serta menyerahkan segala keputusan kepada pimpinan.', 'E', 'B', 'C', 'A', 'D', 'A'),
(2, 'Saya dipercayakan mengelola kegiatan yang belum dipublikasikan dan masih harus dijaga kerahasiannya. Ketika saya berada di antara teman-teman dekat di kantor, saya ...', 'Membatasi pembicaraan agar tidak menyangkut ke hal-hal tugas baru saya.', 'Akan merasa gelisah dan kurang senang bila mereka mulai membicarakan tugas baru saya.', 'Akan mengalihkan ke pembicaraan lain bila mereka sudah mulai menyinggung tugas baru saya.', 'Suka menerima masukan dalam rangka pengembangan tugas baru saya.', 'Membicarakan hal-hal lain yang tidak ada kaitannya dengan tugas baru saya.', 'D', 'E', 'C', 'A', 'B', 'A'),
(3, 'Kerja keras dan cermat merupakan wujud upaya untuk menjadi pribadi yang bermanfaat bagi organisasi. Berkaitan dengan hal tersebut, saya senang ...', 'Pekerjaan yang menantang.', 'Bekerja tanpa mengenal lelah dan pamrih.', 'Bekerja dengan standar hasil yang tinggi.', 'Pekerjaan yang menumbuhkan kreativitas baru.', 'Pekerjaan yang rutin.', 'D', 'B', 'C', 'A', 'E', 'A'),
(4, 'Ketika sedang melakukan presentasi, saya menerima pesan singkat (SMS) yang mengabarkan anak saya diopname di rumah sakit. Reaksi saya ...', 'Melanjutkan presentasi.', 'Menghentikan presentasi dan mencari tahu kondisi anak saya.', 'Menghentikan presentasi dan langsung menuju rumah sakit.', 'Mencari tahu kondisi anak saya kemudian memutuskan apakah tetap presentasi atau ke rumah sakit.', 'Membalas SMS dan melanjutkan presentasi.', 'D', 'B', 'E', 'C', 'A', 'A'),
(5, 'Saya baru saja dimutasikan ke unit lain yang sama sekali baru bagi saya. Sikap saya ...', 'Berusaha memahami mekanisme kerja unit melalui arsip dan aturan kebijakan.', 'Duduk-duduk saja sambil menunggu perintah atasan.', 'Berusaha mempelajari dan memahami mekanisme kerja unit melalui rekan sejawat.', 'Mengamati proses pekerjaan yang dilakukan rekan sejawat.', 'Jarang masuk karena belum jelas apa yang harus dikerjakan.', 'A', 'C', 'D', 'B', 'E', 'A'),
(6, 'Sikap saya terhadap perubahan-perubahan, ide-ide baru, cara baru dalam melaksanakan suatu pekerjaan adalah ...', 'Dengan adanya perubahan, kondisi kerja pasti lebih baik.', 'Keberhasilan pekerjaan tergantung jenis perubahan, ide, dan cara-cara baru tersebut.', 'Stabilitas dalam bekerja lebih penting.', 'Perubahan adalah sesuatu yang pasti.', 'Perubahan bukan jaminan keberhasilan pekerjaan.', 'A', 'B', 'D', 'E', 'C', 'A'),
(7, 'Terjadi pergantian pimpinan di unit kerja saya. Sikap saya...', 'Berusaha mengenal dan memahamivisi dan misi pimpinan yang baru.', 'Tidak berusaha mendekati pimpinan baru karena takut dikatakan penjilat.', 'Berusaha mengenal pribadi pimpinan baru.', 'Pergantian pimpinan itu suatu yang biasa.', 'Tidak peduli.', 'A', 'D', 'C', 'B', 'E', 'A'),
(8, 'Saya diutus mengikuti suatu diklat. Oleh panitia penyelenggara saya ditempatkan sekamar dengan orang yang tidak saya kenal yang berasal dari kota lain. Sikap saya ...', 'Mengajukan keberatan dan minta ditempatkan dengan minimal orang yang dikenal.', 'Menerima aturan panitia dan berusaha mengenal dan memahami teman sekamar.', 'Mengajukan keberatan tetapi akhirnya menerima aturan panitia.', 'Protes keras dan minta ditempatkan sendiri saja.', 'Menerima aturan panitia.', 'B', 'E', 'C', 'A', 'D', 'A'),
(9, 'Dalam rapat staf dan pimpinan, pendapat saya dikritik kelas oleh peserta rapat lainnya. Respons saya ...', 'Mencoba mempelajari kritikan tersebut dan berbalik mengkritik tajam.', 'Menerima kritikan tersebut sebagai masukan.', 'Mencoba sekuat tenaga mempertahankan pendapat saya.', 'Menyerang semua peserta yang mengkritik pendapat saya.', 'Diam saja', 'B', 'C', 'A', 'E', 'D', 'A'),
(10, 'Saya ditugaskan di <i>front office</i> untuk melayani tamu pimpinan. Pada saat pimpinan saya tidak berada di tempat dan ada tamu pimpianan yang memerlukan keputusan segera, sikap saya adalah ...', 'Mengambil keputusan tanpa petunjuk atasan selama tidak bertentangan dengan kebijakan umum pimpinan.', 'Tidak berani mengambil keputusan tanpa petunjuk atasan saya.', 'Ragu-ragu dalam mengambilkeputusan tanpa petunjuk atasan saya.', 'Menunda-nunda pengambilan keputusan tanpa petunjukan atasan saya.', 'Mengambil keputusan tanpa petunjuk atasan karena keadaan mendesak.', 'A', 'E', 'D', 'C', 'B', 'A'),
(11, 'Saya ditawarkan oleh pimpinan untuk melanjutkan studi di provinsi lain atas biaya kantor. Saat inisaya baru saja dikaruniai bayi kembar dan masih membutuhkan perhatian saya. Keputusan saya ...', 'Menerima tawaran tersebut dengan permohonan agar keluarga dapat ikut saya dengan tambahan biaya hidup.', 'Menolak tawaran tersebut namun mohon kebijakan pimpinan jika ada kesempatan lagi saya dapat diikutkan.', 'Menerima tawaran tersebut dan dengan berat hati meninggalkan keluarga.', 'Menolak tawaran tersebut.', 'Menerima tawaran tersebut dengan konsekuensi mengikutkan keluarga merupakan tanggung jawab saya.', 'E', 'A', 'C', 'B', 'D', 'A'),
(12, 'Draft laporan yang dibuat oleh tim kerja saya ditolak oleh atasan karena dianggap kurang visibel (layak). Sikap saya adalah ...', 'Menerima penolakan tersebut tetapi berusaha memperbaiki.', 'Menerima penolakan tetapi tidak melakukan tindak lanjut.', 'Berusaha mencari alasan seperti sedikitnya waktu untuk mengerjakannya.', 'Segera melakukan perbaikan atas draft laporan dan mengajukan kembali.', 'Menyalahkan rekan sejawat yang sama-sama mengerjakannya.', 'D', 'A', 'B', 'C', 'E', 'A'),
(13, 'Saya ditugaskan untuk memimpin tim kerja dengan batasan waktu yang sangat ketat. Anggota tim kerja memperlihatkan sikap tidak peduli dengan tugas yang diemban. Sikap saya adalah ...', 'Bekerja sendiri asalkan tugas selesai.', 'Mengancam anggota yang tidak serius tersebut akan dikeluarkan dari tim.', 'Melaporkan pada atasan saya agar diberi sanksi.', 'Membagi tugas secara adil dan memotivasi serta menegur anggota untuk menyelesaikannya.', 'Menasihatinya agar sadar akan penyelesaian tugas yang diemban.', 'D', 'E', 'A', 'C', 'B', 'A'),
(14, 'Saya sedang mengerjakan pekerjaan kantor, yang harus selesai besok pagi. Tiba-tiba teman datang dengan wajah cemberut dan tampaknya ingin mengeluarkan isi hatinya kepada saya. Atas kejadian itu saya ...', 'Meninggalkan pekerjaan saya serta meberi berbagai alternatif penyelesainnya.', 'Tetap mengerjakan sambil mendengar ceritanya.', 'Meneruskan pekerjaan dan berusaha untul tidak memperdulikan keinginan teman saya.', 'Memohon kepadanya untuk tidak mengganggu saya karena sedang sibuk.', 'Meninggalkan pekerjaan saya dan mendengarkan ceritanya dengan penuh perhatian.', 'B', 'D', 'C', 'A', 'E', 'A'),
(15, 'Organisasi sedang mengalami permasalahan internal seputar manajemen keuangan. Pendapat saya terhadap kondisi ini ...', 'Pastikan bahwa kepala keuangan bertanggung jawab penuh terhadap masalah ini.', 'Perlu membeberkan permaslahan kepada seluruh jajaran organisasi.', 'Saya akan menjaga kerahasiaan permasalahan yang terjadi dan mencoba memberikan alternatif solusi kepada pimpinan.', 'Seharusnya pimpinan puncak dapat menindak tegas yang terlibat dalam masalah ini.', 'Tidak mempersoalkan masalah tersebut karena bukan bagian tugas saya.', 'C', 'A', 'D', 'E', 'B', 'A'),
(16, 'Anda adalah seorang karyawan opotek. Seorang pembeli ingin membeli obat-obatan tertentu yang harus menggunakan resep dokter karena bisa membahayakan kesehata. Dia tidak mempunyai resrp itu. Namun, pembeli tersebut memaksa ingin membelinya dan dia memberikan sejumlah uang kepad Anda agar mau memeberikan obat tersebut. Apa yang harus Anda lakukan?', 'Saya memberikan obat tersebut kepadanya, toh tidak ada yang tahu.', 'Saya ragu-ragu keputusan apa yang saya ambil.', 'Saya berkonsultasi kepada rekan sejawat dulu.', 'Saya menolaknya dengan mantap.', 'Saya menerima uang tersebut dan memberikan obatnya.', 'D', 'C', 'B', 'A', 'E', 'A'),
(17, 'Andi, teman karib Anda, melakukan kecurangan absensi. Maka Anda ...', 'Menoleransi sebab baru kali ini Andi melakukannya.', 'Rekan kerja yang lain juga melakukannya, jadi tidaklah mengapa.', 'Mengingatkan dan menegur.', 'Menegur dan meleporkan apa adanya kepada atasan.', 'Menanyakan kepadanya mengapa dia melakukan hal tersebut', 'C', 'E', 'D', 'A', 'B', 'A'),
(18, 'Saya ditugaskan pimpinan untuk menjadi notulis dalam Rapat Badan Pertimbangan Jabatan dan Kepangkatan. Respon saya adalah ...', 'Berusaha menghindari rekan yang membujuk untuk mengetahui hasil keputusan.', 'Dengan bangga saya akan menceritakan kepada rekan sejawat saya hasil keputusan rapat.', 'Memberitahukan anggota keluaran tentang hasil keputusan rapat.', 'Memberitahu sahabat saya di kantor tentang hasil keputusan rapat.', 'Tidak akan membocorkan hasil.', 'E', 'A', 'C', 'D', 'B', 'A'),
(19, 'Tempat saya bekerja sedang mengalami permasalahan keuangan berupa kerugian atau defisit yang cukup besar. Pendapat saya terhadap kondisi ini adalah ...', 'Saya akan menjaga kerahasiaan permasalahan yang terjadi dan tidak ingin ikut campur masalah keuangan.', 'Seharusnya pimpinan puncak dapat menindak tegas yang terlibat dalam masalah ini.', 'Tidak mempersoalkan masalah tersebut karena bukan bagian tugas saya.', 'Pastikan bahwa kepala keuangan bertanggung jawab penuh terhadap masalah ini.', 'Perlu menjelaskan permasalahan kondisi keuangan perusahaan kepada seluruh jajaran organisasi agar dapat mengevaluasi kembalu rencana pembelanjaan.', 'E', 'D', 'B', 'A', 'C', 'A'),
(20, 'Bagi saya, bekerja adalah ...', 'Beribadah.', 'Tugas.', 'Kewajiban.', 'Kebutuhan.', 'Mencari uang untuk nafkah.', 'A', 'D', 'E', 'C', 'B', 'A'),
(21, 'Dalam suatu pertemuan atau rapat, teman Anda melakukan kecurangan absensi. Yang Anda lakukan adalah ...', 'Membiarkannya saja.', 'Melaporkannya pada pimpinan/ketua rapat.', 'Memberitahukan pada teman terdekat.', 'Diam saja pura-pura tidak lihat.', 'Ikut-ikutan melakukannya kemudian bolos dari rapat.', 'B', 'C', 'A', 'D', 'E', 'A'),
(22, 'Anda diminta oleh Direktur perusahaan untuk menjadi ketua sekaligus penanggung jawab sebuah tim di kantor Anda, padahal Anda merasa belum pantas sebab masih masih ada manajer yang Anda anggap mampu. Sikap yang harus Anda lakukan adalah ...', 'Menolak permintaan direktur tersebut.', 'Mempertimbangkan permintaan tersebut dan minta saran/masukan dari manajer Anda.', 'Menerima permintaan tersebut dengan senang hati.', 'Menolak permintaan dan mengusulkan manajer Anda saja kepada Direktur.', 'Menerima tawaran tersebut agar Anda bisa naik jabatan sejajar manajer Anda.', 'B', 'C', 'D', 'E', 'A', 'A'),
(23, 'Seorang staf HRD di sebuah instansi menawarkan kepada Anda bahwa Anda bisa diterima sebagai karyawan di perusahaan tersebut. Namun, Anda dimintai sejumlah dana agar proses tersebut berjalan lancar. Sikap Anda adalah ...', 'Memberikan dana tersebut asal Anda bisa bekerja di perusahaan itu.', 'Menolak tawaran itu dan mencoba melamar dengan yang baik/ikut seleksi.', 'Mempertimbangkan tawaran itu.', 'Melaporkan ulah HRD itu kepada pimpinan perusahaan.', 'Menerima tawaran itu, toh tidak ada orang yang tahu.', 'B', 'D', 'C', 'E', 'A', 'A'),
(24, 'Sebagian rekan Anda pulang lebih awal sekitar 30 menit dari jadwal kantor. Sikap Anda ...', 'Ikut pulang saja.', 'Membiarkan mereke pulang dulu karena pekerjaan Anda belum selesai.', 'Tetap pulang sesuai jadwal yang ditetapkan.', 'Segera menyelesaikan pekerjaan dan menyusul pulang.', 'Melaporkannya pada atasan keesokan harinya', 'C', 'E', 'B', 'D', 'A', 'A'),
(25, 'Dalam suatu diskusi, pendapat Anda ditolak oleh atasan. Sikap Anda adalah ...', 'Menerima dengan tenang.', 'Tidak terima dan minta penjelasan atasan.', 'Kecewa dan lain kali tidak akan menyampaikan pendapat lagi.', 'Menerima dan minta penjelasan kenapa pendapat itu ditolak.', 'Kecewa dan mencoba bertanya ide atasan yang lebih baik.', 'D', 'A', 'E', 'C', 'B', 'A'),
(26, 'Anda adalah seorang kepala instansi yang cukup bergensi. Suatu ketika ada teman akrab Anda datang meminta bantuan pada Anda agar menerima anaknya bekerja di instansi Anda tanpa melalui tes. Rekan karib Anda tersebut bersedian memberikan jaminan sejumlah uang dan fasilitas. Apa yang akan Anda lakukan? ', 'Menerima tawaran itu tanpa jaminan sebab dia sahabat karib Anda.', 'Basa-basi dulu lalu menerimanya karena tidak enak pada teman.', 'Menolaknya mentah-mentah.', 'Menolaknya secara halus dan menyarankan agar anak rekan Anda mengikuti seleksi seperti lainnya.', 'Menerimanya dan menyuruhnya mengikuti tes seleksi sebagai formalitas.', 'D', 'C', 'A', 'B', 'E', 'A'),
(27, 'Disuatu supermarket, Anda melihat seorang kasir yang melakukan kecurangan terhadap tunanetra. Yang sebaiknya Anda lakukan adalah ...', 'Membiarkannya.', 'Menegur dan mengingatkan kasir agar tidak berlaku curang.', 'Cuek saja karena bukan urusan Anda.', 'Memberitahukan pada pembeli bahwa ia dicurangi.', 'Melaporkan kasir pada atasan/pemilik supermarket.', 'B', 'D', 'E', 'A', 'C', 'A'),
(28, 'Saya sering mengingatkan bawahan saya untuk tidak melakukan kekeliruan pekerjaan kantor ...', 'Saya pun tidak boleh melakukan kekeliruan tersebut.', 'Karena saya atasannya, peraturan tersebut tidak berlaku bagi saya sendiri.', 'Saya sesekali melakukan kekeliruan tersebut.', 'Peraturan tersebut khusus untuk pegawai setingkat dia.', 'Lebih baik saya tidak melakukan kekeliruan tersebut.', 'A', 'E', 'D', 'B', 'C', 'A'),
(29, 'Ketika di kantor, saya tiba-tiba ingin mem-print tiga lembar data-data pribadi yang tidak ada hubungannya dengan pekerjaan ...', 'Maka saya langsung saja mem-print di kantor.', 'Saya mem-print di kantor namun dengan memakai kertas saya sendiri.', 'Saya mem-print di kantor namun menunggu setelah jam kerja selesai.', 'Saya mem-print di rumah saya memakai printer sendiri.', 'Saya mem-print di kantor pada saat printer tidak dipakai.', 'D', 'B', 'E', 'C', 'A', 'A'),
(30, 'Ketika banyak pegawai di kantor saya tidak mematuhi peraturan tentang larangan penggunaan fasilitas internet kantor untuk keperluan pribadi, pendapat saya ...', 'Saya pun tidak akan mematuhi peraturan yang sangat sulit untuk dipatuhi itu.', 'Jika hanya saya yang mematuhi peraturan tersebut, maka percuma saja.', 'Saya akan terus mematuhi peraturan tersebut meskipun sebagian besar melanggarnya.', 'Saya mematuhi peraturan tersebut dan berusaha mengajak pegawai lain untuk turut mematuhinya.', 'Sebaiknya peraturan yang sulit seperti itu harus diubah.', 'D', 'C', 'B', 'E', 'A', 'A'),
(31, 'Ketika seseorang memotong dalam antrean, apa yang akan Anda lakukan?', 'Selalu mengabaikannya begitu saja.', 'Lebih sering saya abaikan saja karena tidak terlalu penting.', 'Tergantung situasi, terkadang saya abaikan terkadang saya tegur.', 'Lebih sering saya tegur karena hal tersebut bukanlah hal yang semestinya.', 'Selalu saya tegur karena semua harus sesai dan adil ', 'E', 'D', 'C', 'B', 'A', 'A'),
(32, 'Apakah anda merasa sulit meminta tolong pada seorang teman untuk melakukan sesuatu untuk Anda?', 'Saya merasa sulit karena saya tidak ingin memberatkan orang lain atas tugas saya.', 'Jika dalam kondisi benar-benar mendesak maka saya akan minta tolong orang lain.', 'Tergantung situasi dan urgensi, terkadang saya meminta tolong terkadang tidak jika tidak dibutuhkan. ', 'Saya jarang meminta tolong karena saya merasa itu tugas saya dan saya berusaha menyelesaikannya sendiri.', 'Saya tidak pernah merasa sulit karena menurut saya meminta tolong adalah hal sopan dan baik.', 'C', 'D', 'E', 'B', 'A', 'A'),
(33, 'Jika atasan Anda meminta suatu permintaan yang tidak masuk akal, apakah Anda sulit untuk mengatakan tidak?', 'Jika tugas tersebut memang tidak dapat dikerjakan, mengapa harus memaksa dikerjakan.', 'Jika tugas tersebut dalam proses hanya dapat diselesaikan sedikit maka kemungkinan saya tolak.', 'Terkadang saya tolak, terkadang saya terima, tergantung bagaimana tugas tersebut dan apakah saya dapat menyelesaikannya.', 'Saya jarang menolak perintah atasan kecuali jika hal tersebut benar-benar tidak mungkin.', 'Saya akan menerima karena walau bagaimanapun juga hal tersebut adalah perintah atasan.', 'C', 'D', 'B', 'A', 'E', 'A'),
(34, 'Apa yang Anda rasakan dan lakukan saat menerima pujian dari atasan Anda?', 'Saya merasa sulit menerima pujian tersebut kareba menurut saya itu berlebihan.', 'Saya merasa sedikit tersipu karena hal tersebut.', 'Saya merasa biasa saja.', 'Saya merasa bersyukur dan puas dengan hasil kerja saya.', 'Saya meras senang dan bangga atas pencapaian saya.', 'D', 'E', 'B', 'C', 'A', 'A'),
(35, 'Apakah Anda secara sukarela memberikan informasi ataupun opini dalam diskusi dengan orang-orang yang tidak Anda kenal dengan baik?', 'Ya, karena mereka adalah orang terdekat saya dan saya percaya.', 'Tergantung seberapa dekat, mungkin untuk keluarga dan sahabat saja.', 'Melihat kondisi terlebih dahulu, seberapa dekat dan seberapa penting informasi tersebut.', 'Saya jarang memberikan informasi kepada orang lain karena takut disalahgunakan.', 'Saya selalu menyimpan opini dan informasi tersebut karena profesionalisme.', 'C', 'E', 'D', 'B', 'A', 'A'),
(36, 'Jika ada seorang <i>public figure</i> yang Anda kagumi dan hormati di sebuah <i>event</i> besar, apa yang akan Anda lakukan?', 'Saya akan memperkenalkan diri, mengajak ngobrol dan bertukar kartu nama.', 'Saya akan cukup bertukar nama, jika saya merasa siap, saya akan berkenalan.', 'Tergantung kesiapan saya, terkadang saya tidak berkenalan dan terkadang berkenalan.', 'Saya jarang memperkenalkan diri kepada orang penting, biasanya hanya meminta foto.', 'Saya tidak pernah berani berkenalan dengan <i>public figure</i>.', 'A', '\r\nB', 'C', 'D', 'E', 'A'),
(37, 'Jika kolega Anda membatalkan janjinya secara tiba-tiba untuk suatu keperluan penting, apa yang akan Anda lakukan?', 'Saya akan menunjukkan betapa kecewanya saya.', 'Jika hal tersebut bukanlah hal yang cukup penting maka saya tidak akan menunjukkan sikap kecewa.', 'Jika hal tersebut dapat ditoleransi dan dia dapat mengetakan dengan jelas maka saya akan biasa saja.', 'Jika saya sangat kecewa, saya tidak dapt menyembunyikannya.', 'Saya selalu dapat menyembunyikan rasa kecewa dan sedih karena tidak ingin orang tahu.', 'C', 'B', 'E', 'D', 'A', 'A'),
(38, 'Jika Anda dalam situasi terburu-buru, sedangkan keadaan membutuhkan Anda mendapatkan sesuatu secara cepat, apa yang Anda lakukan?', 'Saya akan memaksa dan berusaha keras agar tetap mendapatkan apa yang saya butuhkan.', 'Saya akan berusaha keras tetapi jika tidak bisa maka saya akan menerimanya.', 'Saya berusaha sebisa mungkin untuk mendapatkannya.', 'Saya akan berusaha sewajarnya, jika memang tidak mungkin apa boleh buat.', 'Jika secara logis tidak mungkin, saya tidak akan mengusahakan lagi.', 'B', 'D', 'E', 'C', 'A', 'A'),
(39, 'Apakah Anda merasa sulit untuk menolak permintaan orang lain?', 'Saya sulit menolak karena saya takut orang tersebut tersinggung.', 'Sebisa mungkin saya menerima permintaan orang lain jika memang bisa.', 'Jika bisa maka saya terima, jika tidak maka tidak saya terima.', 'Saya jarang menerima permintaan orang lain yang menurut saya tidak terlalu penting.', 'Saya selalu menolak permintaan orang lain jika tidak penting.', 'C', 'B', 'D', 'E', 'A', 'A'),
(40, 'Jika atasan Anda mengungkapkan pendapat yang sangat tidak Anda setujui, apa yang akan Anda lakukan?', 'Saya akan menyatakan pendapat saya sejujurnya.', 'Saya akan menyatakan pendapat saya secara tersirat.', 'Saya akan menyatakan secara tersirat dan memberi solusi tanpa terlihat mengarahkan.', 'Saya jarang mengungkapkan opini yang bertentangan.', 'Saya akan menyetujui walaupun bertentangan denagn opini beliau karena beliau adalah atasan saya yang harus saya hormati.', 'C', 'B', 'A', 'E', 'D', 'A'),
(41, 'Apakah Anda bersikeras agar orang lain melakukan pembagian secara adil?', 'Jelas, karena keadilan adalah utama dan mutlak.', 'Tergantung apakah hal tersebut dapat ditoleransi atau tidak.', 'Melihat kondisi, jika kedua pihak puas walaupun bukan pada posisi yang adil maka tidak masalah.', 'Walaupun tidak adil, jika proporsinya cukup dan berjalan dengan baik tidak masalah.', 'Saya tidak pernah memaksa harus dibagi secara adil.', 'A', 'C', 'D', 'B', 'E', 'A'),
(42, 'Apa yang Anda lakukan dalam sebuah diskusi dengan kelompok kecil dari teman-teman Anda?', 'Saya adalah pendengar dan pengamat yang baik dalam diskusi.', 'Saya lebih banyak mendengar dan berbicara sekadarnya.', 'Jika perlu bicara maka saya utarakan, jika tidak maka saya cukup mendengar.', 'Saya akan menjadi pengamat sejenak lalu aktif berpendapat.', 'Saya selalu aktif berpendapat dan mengarahkan teman-teman untuk berpendapat juga.', 'D', 'E', 'C', 'B', 'A', 'A'),
(43, 'Bagaimana perasaan Anda jika memiliki pendapat yang berbeda dengan orang lain?', 'Saya merasa aman karena memang sudah seharusnya ada banyak pendapat mengenai suatu hal.', 'Saya akan mendengarkan dan memperhatikan lalu memikirkan kembali, mungkin saya dapat menerimanya.', 'Terkadang saya memikirkan ulang pendapat tersebut dan mencari jalan tengahnya.', 'Saya akan memikirkannya lalu mempertimbangkan pendapat yang lebih baik.', 'Saya akan bersikeras memepertahankan argumen saya.', 'C', 'D', 'B', 'A', 'E', 'A'),
(44, 'Apakah Anda menunjukkan inisiatif dan berusaha untuk mengejar prestasi kinerja?', 'Saya selalu menunjukkan inisiatif dalam bekerja.', 'Terkadang jika terpikirkan ide, saya akan berinisiatif melakukan sesuatu.', 'Tergantung <i>mood</i> dan kondisi pribadi saya.', 'Saya jarang menunjukkan inisiatif, tetapi pernah melakukannya.', 'Saya tidak berinisatif, tetapi melakukan tugas yang ada sebaik mungkin.', 'A', 'B', 'C', 'D', 'E', 'A'),
(45, 'Dalam suatu kelompok, apa yang selalu Anda sampaikan tentang diri Anda?', 'Saya memiliki percaya diri yang tinggi dan berkelas.', 'Saya ingin menjadi pusat perhatian dan tampil menonjol.', 'Saya menjadi pihak tengah yang biasa saja.', 'Saya ingin menjadi pengamat dan pendengar yang baik.', 'Saya mengikuti alur yang telah ada.', 'A', 'E', 'C', 'D', 'B', 'A');
INSERT INTO `twk` (`id`, `soal`, `pilihan_a`, `pilihan_b`, `pilihan_c`, `pilihan_d`, `pilihan_e`, `gambar`, `jawaban`) VALUES
(1, 'Sila pertama Pancasila: Ketuhanan Yang Maha Esa, sebagaimana terdapat dalam pembukaan UUD 1945 telah memberikan sifat yang khas kepada negara kebangsaan Indonesia, yaitu ...', 'Merupakan negara sekuler.', 'Merupakan negara agama.', 'Bukan merupakan negara sekuler maupun negara agama.', 'Merupakan negara agraris.', 'Merupakan negara Maritim', '', 'C'),
(2, 'Nilai instrumental dalam ideologi Pancasila adalah ...', 'Saling menghormati.', 'Keadilan.', 'Ketuhanan.', 'Peraturan-peraturan.', 'Kemanusiaan.', '', 'D'),
(3, 'Pembangunan Indonesia yang dilaksanakan pada standar nilai Pancasila, merupakan maksud dari ...', 'Pancasila sebagai deologi tebuka.', 'Pancasila sebagai ideologi tertutup.', 'Pancasila sebagai nilai instrumental.', 'Pancasila sebagai dasar negara.', 'Pancasila sebagai paradigma pembangunan.', '', 'E'),
(4, 'Pancasila sebagai jiwa dan kepribadian bangsa, maksudnya adalah ...', 'Bangsa Indonesia memiliki ciri khas yang tidak dimiliki oleh bangsa lain', 'Kehidupan Bangsa Indonesia sesuai dengan kepribadiannya.', 'Pembangunan dilaksanakan mengacu pada standar nilai Pancasila.', 'Selau menunjukkan bangsa dan negaranya.', 'Menjadi sumber ukuran baik atau buruk adalah kepribadiannya.', '', 'A'),
(5, 'Pancasila termaktub dalam pembukaan Undang-undang Dasar 1945 pada ...', 'Alinea pertama.', 'Alinea kedua.', 'Alinea ketiga.', 'Alinea keempat.', 'Alinea ketiga dan keempat.', '', 'D'),
(6, 'Pada awal kemerdekaan, Negara Indonesia mempunyai infrapolitik lengkap, yaitu lembaga negara yang mestinya ada sesuai UUD 1945. Berdasarkan aturan peralihan UUD 1945 pasal IV, kekuasaan lembaga negara dijalnkan oleh ...', 'Perdana Menteri.', 'Presiden.', 'Wakil Presiden.', 'BPUPKI.', 'PPKI.', '', 'B'),
(7, 'Undang-undang Dasar 1945 sebelum diamandemen menegaskan bahwa kedaulatan diatur di tangan rakyat dan dilakukan sepenuhnya oleh ...', 'Mahkamah Agung.', 'Perdana Menteri.', 'Presiden.', 'Majelis Permusyawarakatan Rakyat.', 'Dewan Perwakilan Rakyat.', '', 'D'),
(8, 'Berikut ini adalah pasal-pasal yang diubah pada Amandemen Kedua UUD 1945, kecuali ...', 'Pasal 18.', 'Pasal 20.', 'Pasal 24.', 'Pasal 28.', 'Pasal 36.', '', 'C'),
(9, 'UUD 1945 sebagai dasar hukum Negara Kesatuan Republik Indonesia dipenuhi perdebatan yang sengit pada proses pembentukannay, terutama anatara founding fathers sebagai berikut, kecuali ...', 'Soepomo.', 'Mohammad Hatta.', 'Mohammad Yamin.', 'Soekarno.', 'H.O.S Tjokroaminoto.', '', 'E'),
(10, 'Konstitusi Indonesia yang dikenal sebagai UUD 1945 dalam konteks klasifikasi konstitusi merupakan konstitusi yang ...', 'Tertulis, fleksibel, dan solid.', 'Tidak tertulis, fleksibel dan rigid.', 'Tertulis, fleksibel, dan rigid.', 'Tidak tertulis, toleran, dan rigid.', 'Tertulis, toleran, dan solid.', '', 'C'),
(11, 'Pendapat sarjana hukum (doktrin) merupakan salah satu sumber hukum formil. Pendapat tersebut dapat dipegang apabila termasuk ...', 'Seseorang atau beberapa orang sarjana hukum yang terkenal dalam ilmu hukum.', 'Sekumpulan orang di lembaga bantuan hukum.', 'Para sarjana muda dari lulusan fakultas hukum.', 'Orang-orang yang bekerja di Mahkamah Konstitusi.', 'Orang yang berkaitan dengan penegak hukum.', '', 'A'),
(12, 'Anggota Komnas HAM yang berjumlah 35 orang dipilih oleh ... ', 'Presiden.', 'DPR.', 'Menteri Hukum dan HAM.', 'Mahkamah Agung.', 'Mahkamah Konstitusi.', '', 'B'),
(13, 'Berdasrkan UU Nomor 48 Tahun 2009 tentang Kekuasaan Kehakiman dijelaskan bahwa pengawasan dalam lembaga peradilan dilakukan oleh 2 lembaga, pengawasan secara internal dilakukan oleh ...', 'Menteri Hukum dan HAM.', 'Mahkamah Konstitusi.', 'Komisi Yudisial.', 'Presiden.', 'Mahkamah Agung.', '', 'E'),
(14, 'Berikut adalah lembaga-lembaga independen yang dasar pembentukannya tidak diatur dalam UUD 1945, kecuali ...', 'Komnas HAM.', 'KPK.', 'Komisi Ombudsman.', 'KPPU.', 'Bank Indonesia.', '', 'E'),
(15, 'Kekuatan di parlemen tidak dapat menjatuhkan pimpinan eksekutif. Keadaan ini menunjukkan sistem perintahan ...', 'Presidensial.', 'Parlementer.', 'Monarki Absolut.', 'Demokrasi Liberal.', 'Quasi Parlementer', '', 'D'),
(16, 'Presiden dan / atau Wakil Presiden dapat dimintakan pertanggungjawabannya secara hukum apabila presiden dan / atau wakil presiden melakukan pelanggaran hukum konstitusi. Dalam hal demikian, presiden dan / atau wakil presiden dapat dituntut pertanggungjawabnnya untuk disidangkan dalam MPR, yaitu sidang gabungan antara ...', 'MA dan MK.', 'KY dan KPK.', 'DPR dan DPD.', 'DPR dan MK.', 'DPR dan MA', '', 'C'),
(17, 'Pengakuan dalam bentuk deklarasi bersama oleh sekelompok negar dan pengakuan yang diberika melalui penerimaan suatu negara baru merupakan definisi dari ...', 'Pengakuan De Jure.', 'Pengakuan De Facto', 'Pengakuan Kolektif.', 'Pengakuan terpisah.', 'Pengakuan mutlak.', '', 'A'),
(18, 'Perbedaan sistem pemerintahan kerajaan dan republik salah satunya adalah ...', 'Pengangkatan masa jabatan kepala negara.', 'Jumlah orang yang memegang kekuasaan.', 'Gaya kekuasaan yang dilaksakan.', 'Tujuan kekuasaan yang dilaksanakan pemerintah.', 'Usaha-usaha yang dilakukan untuk mencapai tujuan negara.', '', 'A'),
(19, 'Kepercayaan animisme di indonesia berakulturasi dengan budaya Hindu-Budha dalam bentuk ...', 'Patung dan Arca.', 'Seni tari.', 'Seni musik.', 'Pahat dan relief.', 'Lukisan.', '', 'A'),
(20, 'Perjanjiaan Renville terjadi pada 17 Januari 1947. Perjajian tersebut berisi tentang ...', 'Kesepakantan Indonesia dan Belanda untuk melakukan perundingan Konferensi Meja Bundar (KMB).', 'Pemerintah Indonesia kembali ke Yogyakarta.', 'Belanda mengakui kedaulatan Indonesia yang terdiri dari Jawa, Madura, dan Sumatera.', 'Melalui PBB, Belanda menyerahkan Irian Barat kepada Indonesia.', 'Indonesia mengakui daerah-daerah yang diduduki Belanda pada Agresi Militer 1.', '', 'E'),
(21, 'Secara singkat dapat disebutkan bahwa ASEAN berdiri karena adanya persamaan nasib dijajah oleh bangsa Barat dan Jepang. Satu-satunya negara ASEAN yang tidak pernah dijajah adalah ...', 'Malaysia.', 'Filipina.', 'Brunei Darussalam.', 'Thailand.', 'Vietnam.', '', 'D'),
(22, 'Berikut ini yang tidak termasuk tahapan perkembangan konstitusi di Indonesia adalah ...', 'Konstitusi UUD 1945.', 'Konstitusi Republik Indonesia Serikat (RIS) 1949.', 'Konstitusi UUDS 1950.', 'Konstitusi UUD Negara RI tahun 1945 (Amandemen).', 'Konstitusi Republik Indonesia Bersatu.', '', 'E'),
(23, 'Pada taggal 5 hingga 8 agustus 1967 terdapat 5 tokoh yang merupakan Menteri luar negeri dari negara-negara Asia Tenggara, mereka mengadakan pertemuan di Bangkok selama 3 hari. Berikut adalah tokoh-tokoh yang dimaksud, kecuai ...', 'Ruslan Abdulgani dari Indonesia.', 'Tun Abdul Razak dari Malaysia.', 'Thanat Khoman dari Thailand.', 'Rajaratnam dari Singapura.', 'Narciso Ramoz dari Filipina.', '', 'A'),
(24, 'Trikora merupakan salah-satu usaha yang dilakukan pemerintah RI dalam rangka ...', 'Pembebasan Irian Barat.', 'Mendirikan negara boneka.', 'Pembebasan Timor-Timur.', 'Mengawasi pergerakan penjajah.', 'Pendirian negara Papua.', '', 'A'),
(25, 'Kitab Bharatayudha diubah oleh Empu Sedah, namun dia tidak dapat meneruskannya karena ...', 'Jatuhnya kerajaan Kediri.', 'Perebutan kekuasaan antara Jenggala dan Kediri.', 'Empu Sedah meninggal dunia.', 'Pergantiaan raja.', 'Munculnya para pujangga baru.', '', 'C'),
(26, 'Salah satu contoh kerja sama negara-negara ASEAN dalam bidang ekonomi adalah ...', 'Meningkatkan kerjasama dalam pencegahan dan penyalahgunaan narkotika.', 'Menyelenggarakan berbagai festival seni dan budaya antar negara-negara ASEAN.', 'Menyelenggarakan penggunaaan proyek pabrik pupuk di Aceh.', 'Memperkenalkan negara-negara ASEAN melalui institusi pendidikan.', 'Meningkatkan kerjasama untuk meanggulangi jumlah penduduk diwilayah ASEAN.', '', 'C'),
(27, 'Berikut adalah agenda pokok pembicaraan KAA, kecuali ...', 'Kerjasama ekonomi.', 'Kerjasama penentuan wilayah.', 'Kerjasama budaya.', 'Masalah perdamain dunia.', 'Masalah kolonialisme dan imprealisme.', '', 'B'),
(28, 'Menurut Moh. Hatta, tujuan politik luar negeri Indonesia adalah ...', 'Mempertahankan kemerdekaan dan menjaga keselamatan negara.', 'Membentuk Negara Kesatuan Republik Indonesia.', 'Membentuk masyarakat yang adil dan makmur.', 'Mencerdaskan kehidupan bangsa', 'Menguatkan perekonomian Indonesia dengan menjalin kerjasama luar negeri.', '', 'A'),
(29, 'Berikut ini yang bukan tahapan dari diadakannya perjanjian internasional adalah ...', 'Negotiatif.', 'Clarification.', 'Signature.', 'Pengesahan.', 'Penandatanganan.', '', 'B'),
(30, 'Politik luar negeri Indonesia berlandaskan beberapa hal, kecuali ...', 'Sila kedua Pancasila.', 'Pembukaan UUD 1945 Alinea kesatu dan keempat.', 'TAP MPR yang membuat GBNH pada bagian hubungan luar negeri dan politik luar negeri.', 'Batang tubuh pasal 11 dan 13, UUD 1945.', 'Pembukaan UUD 1945 Alinea kedua', '', 'E');
